mapper.select:
SELECT user_id userId, MAX(user_name) userName, MIN( spend_time ) spendTime, MAX(error_count) errorCount FROM (SELECT * FROM t_exam_main_log WHERE date=#{date}
AND user_name=#{userName}
AND dept_name=#{deptName}
AND status=1 ORDER BY error_count,spend_time LIMIT 1000) t GROUP BY t.user_id

SELECT user_id userId, MAX(user_name) userName, COUNT(0) changeCount, MIN( spend_time ) spendTime, SUM(error_count) sumErrorCount, AVG(error_count) avgErrorCount, MAX(dept_name) deptName, MAX(error_count) errorCount FROM (SELECT * FROM t_exam_main_log WHERE YEAR = #{year} AND month=#{month}
AND user_name=#{userName}
AND dept_name=#{deptName}
AND status=1 ORDER BY error_count,spend_time LIMIT 1000) t GROUP BY t.user_id

SELECT MAX(dept_name) deptName, AVG( spend_time ) spendTime, AVG(error_count) errorCount, MAX(spend_time) spendTime FROM t_exam_main_log WHERE date BETWEEN #{startDate} AND #{endDate}
AND user_name=#{userName}
AND dept_name=#{deptName}
AND status=1 GROUP BY dept_id ORDER BY errorCount,spendTime

SELECT question_id,SUM(CASE WHEN t.iscorrect=0 THEN 1 ELSE 0 END) errorCount, COUNT(0) sumCount
FROM t_exam_sub_log t
WHERE date BETWEEN #{startDate} AND #{endDate}

    AND user_name=#{userName}


    AND dept_name=#{deptName}

GROUP BY question_id;
